using Base
using Optim
Base.setprecision(1024)
f(x) = -(375x^33-289x^84)
res = optimize(x->f(first(x)), [Base.big(0.2)], Optim.BFGS(), Optim.Options(g_tol=1e-300))
print(first(Optim.minimizer(res)), "\n")

